def monthlyORyears_sales(data, time=['monthly', 'years']):
    data = data.copy()
    if time == "monthly":
        # Drop the day indicator from the date column:
        data.date = data.date.apply(lambda x: str(x)[:-3])
    else:
        # Keep only the year, but format it as YYYY-01-01 for pd.to_datetime
        # Ensure the date is in the correct format before extracting the year
        data.date = pd.to_datetime(data.date, format='%d-%m-%Y', errors='coerce').dt.strftime('%Y-%m-%d')  # Handle errors
        data.date = data.date.apply(lambda x: str(x)[:4] + '-01-01')

    # Sum sales per month:
    data = data.groupby('date')['sales'].sum().reset_index()
    data.date = pd.to_datetime(data.date, errors='coerce')  # Handle errors during final conversion

    # Drop rows with NaT (Not a Time) in the 'date' column if necessary
    data = data.dropna(subset=['date'])

    return data

