def plots_lag(data, lags=None):
    """Convert dataframe to datetime index"""
    dt_data = data.set_index('date').drop('sales', axis=1)
    dt_data.dropna(axis=0)


    law  = plt.subplot(122)
    acf  = plt.subplot(221)
    pacf = plt.subplot(223)

    dt_data.plot(ax=law, figsize=(15, 5), color='orange')
    # Plot the autocorrelation function:
    smt.graphics.plot_acf(dt_data, lags=lags, ax=acf, color='mediumblue')

    # Calculate maximum allowable lags for PACF:
    max_lags = len(dt_data) // 2 -1  # Ensure lags is less than half the data length

    #Adjust lags if necessary:
    lags = min(lags, max_lags) if lags is not None else max_lags # Use max allowed lags or user input, whichever is smaller

    smt.graphics.plot_pacf(dt_data, lags=lags, ax=pacf, color='mediumblue')

    # Will also adjust spacing between subplots to minimize the overlaps:
    plt.tight_layout()

