import pandas as pd
import statsmodels.api as sm
import numpy as np
import matplotlib.pyplot as plt
from sklearn.metrics import mean_squared_error, mean_absolute_error, r2_score

def sarimax_model(data):
    # Fit the SARIMAX model
    sar = sm.tsa.statespace.SARIMAX(data.sales_diff, order=(1,1,1), seasonal_order=(0,1,1,4), trend='c').fit()


    # Generate predictions (fixed start & end to match dataset size)
    start, end, dynamic = 5, 11, False
    data['pred_value'] = sar.predict(start=start, end=end, dynamic=dynamic)

    # Fill NaN values in predictions (if any)
    data['pred_value'].fillna(0, inplace=True)

    # Plot predictions
    data[["sales_diff", "pred_value"]].plot(color=['blue', 'red'])
    plt.legend(loc='upper left')
    plt.show()

    # Model performance metrics
    rmse = np.sqrt(mean_squared_error(data.sales_diff[start:end], data.pred_value[start:end]))
    mae = mean_absolute_error(data.sales_diff[start:end], data.pred_value[start:end])
    r2 = r2_score(data.sales_diff[start:end], data.pred_value[start:end])

    print(f"RMSE: {rmse}\nMAE: {mae}\nR2 Score: {r2}")

    return sar, data, data['pred_value']  # Now it returns 3 values

