import pickle
import pandas as pd
import numpy as np
import os
import matplotlib.pyplot as plt
import seaborn as sns

def create_results_df():
    #  Check if pickle files exist before loading
    if not os.path.exists("model_scores.p") or not os.path.exists("ARIMAmodel_scores.p"):
        raise FileNotFoundError("Pickle files 'model_scores.p' and 'ARIMAmodel_scores.p' not found!")

    #  Load pickle files safely
    results_dict = pickle.load(open("model_scores.p", "rb"))
    results_dict.update(pickle.load(open("ARIMAmodel_scores.p", "rb")))

    #  Debugging: Check dictionary structure
    print("Loaded results_dict:", results_dict)

    #  Convert dictionary to DataFrame
    results_df = pd.DataFrame.from_dict(results_dict, orient='index', columns=['RMSE', 'MAE', 'R2'])

    #  Sort DataFrame & Reset Index
    results_df = results_df.sort_values(by='RMSE', ascending=False).reset_index()

    #  Save as CSV
    results_df.to_csv('./results.csv', index=False)

    #  Plot Model Comparison
    fig, ax = plt.subplots(figsize=(12, 5))
    sns.lineplot(x=np.arange(len(results_df)), y='RMSE', data=results_df, ax=ax, label='RMSE', color='darkblue')
    sns.lineplot(x=np.arange(len(results_df)), y='MAE', data=results_df, ax=ax, label='MAE', color='cyan')

    #  Fix X-axis tick labels issue
    ax.set_xticks(np.arange(len(results_df)))
    ax.set_xticklabels(results_df['index'], rotation=45)

    ax.set(xlabel="Model", ylabel="Scores", title="Model Error Comparison")
    sns.despine()

    #  Ensure directory exists before saving
    os.makedirs('./model_output', exist_ok=True)
    plt.savefig('./model_output/compare_models.png')
    plt.show()

    return results_df

#  Run the function
results = create_results_df()
print(results)

